mapper.insert:
INSERT INTO EMPLOYEES (EMPLOYEE_ID,
FIRST_NAME,
LAST_NAME, EMAIL, HIRE_DATE, JOB_ID
,MANAGER_ID
,DEPARTMENT_ID
) VALUES( #{employee_id},
#{first_name},
#{last_name}, #{email}, #{hire_date}, #{job_id}
,#{manager_id}
,#{department_id}
)

mapper.update:
UPDATE EMPLOYEES

	LAST_NAME = #{last_name},
	EMAIL = #{email},
	JOB_ID = #{job_id},
	MANAGER_ID = #{manager_id}
	DEPARTMENT_ID = #{department_id}

WHERE EMPLOYEE_ID = #{employee_id}

mapper.select:
SELECT e.employee_id, e.last_name, e.email, e.salary, d.department_id, d.department_name, j.job_title FROM EMPLOYEES e,DEPARTMENTS d, jobs j WHERE e.department_id= d.department_id AND (e.job_id = j.job_id)
DEPARTMENT_ID = #{department_id}
AND JOB_ID = #{job_id}
AND upper(FIRST_NAME) LIKE '%' || upper(#{first_name}) || '%'
